    list-style: none;
    padding: 0 1rem;
    position: relative;
}

.gallery li {
    /*flex: 0 0 calc(25% - 1rem);*/
    /*position: relative;*/
    background: #fff;
    border-radius: 18px;
    border: 3px solid #fff;
    overflow: hidden;
    box-shadow: 0 2px 8px rgba(0,0,0,0.08);
    transition: transform 0.5s ease, box-shadow 0.5s;
}

.gallery li:hover {
    transform: scale(1.05);
    box-shadow: 5px 5px 15px rgba(0,0,0,0.2);
}

.gallery img {
    width: 200px;
    height: 150px;
    display: block;
    object-fit: cover;
    transition: none;
    border-radius: 14px;
}

.gallery img:hover {
    /* 只保留 box-shadow 變化，縮放交給 li */
    box-shadow: 5px 5px 15px rgba(0,0,0,0.2);
    transform: none;
}

/* 計時器樣式 */
.hover-timer {
    position: absolute;
    top: 8px;
    left: 8px;
    background: rgba(0,0,0,0.6);
    color: #fff;
    padding: 2px 8px;
    border-radius: 8px;
    font-size: 1rem;
    z-index: 2;
    pointer-events: none;
    font-family: monospace;
    display: none;
}

.gallery li:hover .hover-timer {
    display: block;
}

